package list;public class linked_list{public single_node head=null;void insert_start(int a){single_node n=new single_node();n.data=a;n.next=head;head=n;}public void insert_end(int a){single_node n=new single_node();n.data=a;if (head==null){head=n;n.next=null;}else{single_node temp=head;while(temp.next!=null)temp=temp.next;n.next=null;temp.next=n;}}public void delete(int a){single_node temp=head,prev=head;while(temp!=null){if(temp.data==a){if(prev==temp)head=temp.next;elseprev.next=temp.next;break;}prev=temp;temp=temp.next;}}public boolean find(int a){boolean exists=false;single_node temp=head;while(temp!=null){if (temp.data==a){exists=true;break;}temp=temp.next;}return exists;}public void display(){System.out.print("[ ");single_node temp=head;while(temp!=null){System.out.print(temp.data+" ");temp=temp.next;}System.out.println("]");}}